export interface IComponent {
  /** 组件名称 */
  name: string;
}

export const ECS_STATUS = {
  STOPPED: 'ecs_status_stopped',
  RUNNING: 'ecs_status_running',
  PAUSED:  'ecs_status_paused'
}

export const ENTITY_EVENTS = {
  ADD: 'ecs_entity_add',
  REMOVE: 'ecs_entity_remove'
}

import { Entity } from './entity';
import { hook } from './game-hook';

class EntityManager {
  /**
   * 实体列表
   */
  public entities: Entity[] = [];

  /**
   * 索引列表
   */
  private indexList: {
    [index: string]: Entity[]
  } = Object.create(null);

  /**
   * 获取实体
   * @param id 实体ID
   */
  get (id: string): Entity | undefined {
    return this.entities.find(e => e.id === id);
  }

  /**
   * 搜索所有ID包含关键词的实体
   * @param key 搜索词
   */
  search (key: string): Entity[] {
    const result: Entity[] = [];
    this.entities.forEach(e => {
      if (e.id.indexOf(key) > -1) {
        result.push(e);
      }
    })
    return result;
  }

  /**
   * 添加实体
   * @param entity 实体
   */
  add (entity: Entity) {
    this.entities.push(entity);
  }

  /**
   * 按照一定规则生成过滤索引名
   * @param filter 过滤器
   */
  getIndexName (filter: string[]): string {
    return filter.sort().join(',') + ','
  }


  /**
   * 获取索引列表
   * @param key 过滤器，通过getIndexName生成
   */
  getIndexList (key: string): Entity[] {
    if (!this.indexList[key]) {
      this.indexList[key] = [];
    }
    return this.indexList[key];
  }

  /**
   * 当某个Entity的组件列表发生变化时，更新索引列表
   * @param action 添加 | 删除组件
   * @param entity Entity
   * @param componentName 组件名称
   */
  updateIndexList (action: 'add' | 'remove', entity: Entity, componentName: string) {
    for (let key in this.indexList) {
      if (key.indexOf(componentName) > -1) {
        const list = this.indexList[key];
        switch (action) {
          case 'add':
            if (this.matchIndex(key, entity.getComponentNames())) {
              list.push(entity);
              hook.emit(key, action, entity);
            }
            break;
          case 'remove':
            const index = list.indexOf(entity);
            if (index > -1) {
              list.splice(index, 1);
              hook.emit(key, action, entity);
            }
            break;
        }
      }
    }
  }


  /**
   * 匹配某个Entity是否属于索引
   * @param indexName
   * @param componentNameList
   */
  private matchIndex (indexName: string, componentNameList: string[]): boolean {
    const indexList = indexName.substr(0, indexName.length - 1).split(',');
    const count = indexList.length;
    let matched = false;
    let checked = 0;
    componentNameList.some(componentName => {
      if (componentName === indexList[checked]) {
        checked ++;
      }
      if (count === checked) {
        matched = true;
        return true;
      } else {
        return false;
      }
    })
    return matched;
  }
}

export default new EntityManager();

import { IComponent } from './component';
import entityManager from './entity-manager';
import { hook } from './game-hook';
import { ENTITY_EVENTS } from './const';

type TComponentList = {
  [index: string]: IComponent | any;
}

export interface IEntity<T> extends Entity {
  component: T;
}

export class Entity {
  public id: string;
  public component: TComponentList = Object.create(null);
  public children: Entity[] = [];

  /**
   * @param id 实体ID
   */
  constructor (id: string) {
    this.id = id;
  }

  add (entity: Entity) {
    this.children.push(entity);
    hook.emit(ENTITY_EVENTS.ADD, this, entity);
  }

  remove (entity: Entity) {
    this.children = this.children.filter(e => e !== entity);
    hook.emit(ENTITY_EVENTS.REMOVE, this, entity);
  }

  getComponentNames (): string[] {
    return Object.keys(this.component).sort();
  }

  getComponent<T> (componentName: string): T | null | undefined {
    return this.component[componentName];
  }

  addComponent (component: IComponent) {
    this.component[component.name] = component;
    entityManager.updateIndexList('add', this, component.name);
  }

  removeComponent (component: IComponent) {
    delete this.component[component.name];
    entityManager.updateIndexList('remove', this, component.name);
  }
}

/**
 * 事件原型
 */
export class EventEmitter {
  protected evts: {
    [index: string]: any[]
  } = Object.create(null);

  on (evtName: string, handler: any) {
    if (!this.evts[evtName]) {
      this.evts[evtName] = [];
    }
    this.evts[evtName].push(handler);
  }

  off (evtName: string, handler: any) {
    if (!this.evts[evtName]) return;
    this.evts[evtName] = this.evts[evtName].filter(h => h !== handler);
  }

  emit (evtName: string, ...args: any[]) {
    const list = this.evts[evtName];
    list && list.forEach(h => h(...args));
  }
}

/**
 * 事件总线
 */
export const hook = new EventEmitter();

export default class GameLoop {
  /**
   * 逻辑调用栈
   */
  private stack: any[] = [];
  /**
   * 是否正在运行
   */
  public running: boolean = false;

  /**
   * 添加任务
   * @param cb
   */
  add (cb: any) {
    this.stack.push(cb);
  }

  /**
   * 移除任务
   * @param cb
   */
  remove (cb: any) {
    const idx = this.stack.indexOf(cb);
    if (idx > -1) {
      this.stack.splice(idx, 1);
    }
  }

  /**
   * 开始循环
   */
  start () {
    this.running = true;
  }

  /**
   * 结束循环
   */
  stop () {
    this.running = false;
  }

  /**
   * 执行循环
   * @param deltaTime
   */
  exec (deltaTime: number) {
    if (this.running) {
      this.stack.forEach(cb => cb(deltaTime));
    }
  }
}

import System from './system';
import { IEntity, Entity } from './entity';
import { Ecs } from './main';
import { ECS_STATUS, ENTITY_EVENTS } from './const';
import { IComponent } from './component';
import { EventEmitter } from './game-hook';

export {
  IComponent,
  ECS_STATUS,
  ENTITY_EVENTS,
  EventEmitter,
  Entity,
  System,
  Ecs,
}

export type IEntity<T> = IEntity<T>;

import System from './system';
import GameLoop from './game-loop';
import { hook } from './game-hook';
import { ECS_STATUS } from './const';

class EcsCore {
  /** 游戏循环 */
  private looper: GameLoop = new GameLoop();
  /** 游戏系统列表 */
  private systems: System[] = [];
  /** 游戏勾子 */
  public hook = hook;
  /** 游戏运行状态 */
  public status: string = ECS_STATUS.STOPPED;

  constructor () {
    this.looper.add(this.updateSystem.bind(this));
  }

  /**
   * 添加系统
   * @param system
   */
  addSystem (system: System) {
    if (this.systems.find(sys => sys.name === system.name)) return;
    this.systems.push(system);
  }

  /**
   * 移除系统
   */
  removeSystem (system: System | string) {
    const idx = typeof system === 'string'
      ? this.systems.findIndex(sys => sys.name === system )
      : this.systems.findIndex(sys => sys.name === system.name);
    if (idx > -1) {
      this.systems.splice(idx, 1);
    }
  }

  /**
   * 更新系统
   * @param deltaTime
   */
  updateSystem (deltaTime: number) {
    // 第一遍帧更新
    for (let sys of this.systems) {
      sys.traverse(deltaTime);
    }
    // 第二遍等全部系统帧更新结束后再次更新
    for (let sys of this.systems) {
      sys.lastUpdated();
    }
  }

  /**
   * 切换游戏状态
   * @param looperStart
   * @param gameStatus
   */
  private changeGameStatus (looperStart: boolean, gameStatus: string) {
    if (this.status === gameStatus) return;
    if (looperStart) {
      this.looper.start();
    } else {
      this.looper.stop();
    }
    this.hook.emit(gameStatus);
    this.status = gameStatus;
  }

  /**
   * 开始游戏
   */
  start () {
    this.changeGameStatus(true, ECS_STATUS.RUNNING);
  }

  /**
   * 暂停游戏
   */
  pause () {
    this.changeGameStatus(false, ECS_STATUS.PAUSED);
  }

  /**
   * 恢复游戏
   */
  resume () {
    this.changeGameStatus(true, ECS_STATUS.RUNNING);
  }

  /**
   * 停止游戏
   */
  stop () {
    this.changeGameStatus(false, ECS_STATUS.STOPPED);
  }

  /**
   * 更新游戏
   * @param deltaTime
   */
  update (deltaTime: number) {
    this.looper.exec(deltaTime);
  }
}

let ecs: EcsCore;

export function Ecs (): EcsCore {
  if (!ecs) ecs = new EcsCore();
  return ecs;
}

import { Entity } from './entity';
import entityManager from './entity-manager';
import { hook } from './game-hook';

export default class System {
  protected entities: Entity[] = [];
  public name: string;
  public hook = hook;

  /**
   * 创建系统
   * ----
   * 通常情况下，系统会遍历所有的实体，当你配置过滤器后，系统只会遍历包含特定组件的实体
   * @param name 系统名称
   * @param componentFilter 组件过滤器
   */
  constructor (name: string, componentFilter?: string[]) {
    this.name = name;
    // 初始化索引
    if (!componentFilter || !componentFilter.length) {
      this.entities = entityManager.entities;
    } else {
      const key = entityManager.getIndexName(componentFilter);
      this.entities = entityManager.getIndexList(key);
      hook.on(key, this.onListChanged.bind(this));
    }
  }

  /**
   * 遍历实体
   * @param deltaTime
   */
  public traverse (deltaTime: number) {
    this.entities.forEach((e, idx) => this.update(e, idx, deltaTime));
  }

  /**
   * 帧更新
   * @param entity 
   * @param index 
   * @param deltaTime 
   */
  public update (entity: Entity, index: number, deltaTime: number) { };

  /**
   * 全部系统更新结束时调用
   */
  public lastUpdated () { };

  /**
   * 当遍历列表发生变化时触发，如某个Entity被添加或移除
   * @param action 添加 | 删除
   * @param entity 对应事件的Entity
   */
  public onListChanged (action: 'add' | 'remove', entity: Entity) { };
}

import { EventEmitter } from '../@core';

interface IScriptOptions {
  action: (...args: any) => any;
  eventName?: string;
  condition?: (...args: any) => boolean;
  autoStart?: boolean;
}

interface IScript extends IScriptOptions {
  // disabled?: boolean;
  actionWithCondition?: (...args: any) => any;
}

class ScriptManager extends EventEmitter {
  private scripts: {
    [index: string]: IScript
  } = {};

  use (scriptName: string, ...args: any) {
    const script = this.scripts[scriptName];
    if (script) {
      script.action(...args);
    }
  }

  register (scriptName: string, scriptOptions: IScriptOptions) {
    if (this.scripts[scriptName]) {
      console.error(`Duplicate Script: ${scriptName} with options:`, scriptOptions);
      return;
    }
    const { eventName, condition, autoStart } = scriptOptions;
    const script: IScript = { ...scriptOptions };
    // Event.
    if (eventName) {
      if (condition) {
        script.actionWithCondition = () => condition && script.action();
        this.on(eventName, script.actionWithCondition);
      } else {
        this.on(eventName, script.action);
      }
    }
    // AutoStart.
    if (autoStart) {
      script.action();
    }
    // Registered.
    this.scripts[scriptName] = script;
  }

  unregister (scriptName: string) {
    const script = this.scripts[scriptName];
    if (!script) return;
    const { eventName, condition, action, actionWithCondition } = script;
    if (eventName) {
      this.off(eventName, condition ? actionWithCondition : action);
    }
    delete this.scripts[scriptName];
  }
}

const scriptManager = new ScriptManager();

export default scriptManager;

import { IComponent } from '../@core';

interface IOptions {
  speed?: number[];
  size?: number;
}

export default class Movement implements IComponent {
  name = 'movement';

  /**
   * 速度 [x, y, z]
   */
  speed = [0, 0, 0];

  /**
   * 碰撞体积
   */
  size = 0;

  constructor (option?: IOptions) {
    if (option) Object.assign(this, option);
  }
}

import { IComponent } from '../@core';

export default class Sprite implements IComponent {
  name = 'sprite'

  /** 
   * 渲染对象
   */
  object: THREE.Object3D | null;

  constructor (obj: THREE.Object3D) {
    this.object = obj;
  }
}

import { Entity } from '../@core';
import { GAME_SCRIPT_NAME, GAME_WORLD_STATUS, GAME_DATA } from '../const';
import scriptManager from '../@extend/script-manager';
import Sprite from '../components/sprite';
import Movement from '../components/movement';
import game from '../game';
import getRandomColor from '../utils/get-random-color';
const { THREE } = window;

function createRoomObject() {
  const geom = new THREE.BoxGeometry(GAME_DATA.ROOM_SIZE, GAME_DATA.ROOM_SIZE, GAME_DATA.ROOM_SIZE);
  const material = new THREE.MeshBasicMaterial({ color: 0x00ff00, wireframe: true });
  material.opacity = 0.5;
  material.transparent = true;
  const cube = new THREE.Mesh(geom, material);
  return cube;
}

function createBallObject (size: number) {
  // With Random Color
  const geom = new THREE.SphereGeometry(size, 16, 16);
  const material = new THREE.MeshBasicMaterial({ color: getRandomColor() });
  const ball = new THREE.Mesh(geom, material);
  return ball;
}

function createBall (index: number) {
  const ball = new Entity('ball_' + index);
  const size = Math.floor(Math.random() * GAME_DATA.BALL_SIZE_RANGE + 8);
  const speed = [Math.random() * 2, Math.random() * 2, Math.random() * 2];
  ball.addComponent(new Sprite(createBallObject(size)));
  ball.addComponent(new Movement({ speed, size }));
  return ball;
}

function InitAction() {
  // Create a room entity & Add to the world.
  const room = new Entity('room');
  room.addComponent(new Sprite(createRoomObject()));
  game.world.add(room);

  // Create Balls
  for (let i = 0; i < GAME_DATA.BALL_NUM; i++) {
    setTimeout(() => room.add(createBall(i)), i * 600);
  }
}

export default scriptManager.register(
  GAME_SCRIPT_NAME.INIT,
  {
    eventName: GAME_WORLD_STATUS.START,
    action: InitAction
  }
)

import { System, IEntity } from '../@core';
import Sprite from '../components/sprite';
import Movement from '../components/movement';
import { GAME_DATA } from '../const';

type TriggeredEntity = IEntity<{
  sprite: Sprite;
  movement: Movement;
}>;

class CollisionSystem extends System {
  constructor () {
    super('CollisionSystem', ['sprite', 'movement']);
  }

  update (entity: TriggeredEntity) {
    // Check Wall.
    const { x, y, z } = entity.component.sprite.object!.position;
    const { speed, size } = entity.component.movement;
    this.hasHitWall(size, x, 0, speed);
    this.hasHitWall(size, y, 1, speed);
    this.hasHitWall(size, z, 2, speed);
  }

  hasHitWall (size: number, value: number, index: number, arr: number[]) {
    const threshold = GAME_DATA.ROOM_SIZE / 2 - size;
    if (value >= threshold || value <= (0 - threshold)) {
      arr[index] = 0 - arr[index];
    }
  }

  isCollided (e1: TriggeredEntity, e2: TriggeredEntity) {
    const { x: x1, y: y1, z: z1 } = e1.component.sprite.object!.position;
    const { x: x2, y: y2, z: z2 } = e2.component.sprite.object!.position;
    const d = Math.pow(x2 - x1, 2) + Math.pow(y2 - y1, 2) + Math.pow(z2 - z1, 2);
    const minD = Math.pow(e1.component.movement.size + e2.component.movement.size, 2);
    if (d <= minD) {
      const swap = e1.component.movement.speed;
      e1.component.movement.speed = e2.component.movement.speed;
      e2.component.movement.speed = swap;
    }
  }

  lastUpdated () {
    // Check Balls.
    for (let i = 0, len = this.entities.length; i < len; i++) {
      const selectedEntity = this.entities[i] as TriggeredEntity;
      for (let j = i + 1; j < len; j++) {
        const comparedEntity = this.entities[j] as TriggeredEntity;
        this.isCollided(selectedEntity, comparedEntity);
      }
    }
  }
}

export default new CollisionSystem();

import { System, IEntity } from '../@core';
import Movement from '../components/movement';
import Sprite from '../components/sprite';

type TriggeredEntity = IEntity<{
  sprite: Sprite;
  movement: Movement;
}>

class MovementSystem extends System {
  constructor () {
    super('MovementSystem', ['movement', 'sprite']);
  }

  update (entity: TriggeredEntity, index: number, deltaTime: number) {
    const { sprite, movement } = entity.component;
    const { object } = sprite;
    if (!object) return;
    const { x, y, z } = object.position;
    const [ dx, dy, dz ] = movement.speed;
    object.position.set(
      x + dx * deltaTime,
      y + dy * deltaTime,
      z + dz * deltaTime
    );
  }
}

export default new MovementSystem();

import { System, ENTITY_EVENTS, IEntity } from '../@core';
import Game from '../game';
import Sprite from '../components/sprite';

type TriggeredEntity = IEntity<{ sprite: Sprite }>

class RenderSystem extends System {
  constructor () {
    super('RenderSystem', ['sprite']);
    this.bindEvents();
  }

  bindEvents () {
    this.hook.on(ENTITY_EVENTS.ADD, this.renderObject.bind(null, 'add'));
    this.hook.on(ENTITY_EVENTS.REMOVE, this.renderObject.bind(null, 'remove'));
  }

  renderObject (action: 'add' | 'remove', parent: TriggeredEntity, child: TriggeredEntity) {
    const s1 = parent.component.sprite;
    const s2 = child.component.sprite;
    if (!s1 || !s2 || !s1.object || !s2.object) return;
    if (action === 'add') {
      s1.object.add(s2.object);
    } else {
      s1.object.remove(s2.object);
    }
  }

  lastUpdated () {
    Game.render();
  }
}

export default new RenderSystem();

export default function getRandomColor () {
  return Math.floor(Math.random() * 0xffffff);
}

