xhr.open('get', 'files.json');
xhr.responseType = 'json';
xhr.onreadystatechange = function () {
  if (xhr.readyState === 4) {
    viewer.loadFiles(xhr.response);
  }
}
xhr.send();

// start Game.
game.start();
